mapper.select:
SELECT c.COURSE_CODE AS COURSE_CODE, c.COURSE_NAME AS COURSE_NAME, c_s.CLASS_DEVISION AS CLASS_DEVISION, d.DEPARTMENT_NAME AS DEPARTMENT_NAME, c_p.GRADE AS GRADE, c.CREDIT AS CREDIT, c_p.COURSE_DEVISION AS COURSE_DEVISION, p.PROFESSOR_NAME AS PROFESSOR_NAME, c_s.COURSE_DAY AS COURSE_DAY, c_s.COURSE_START_TIME AS COURSE_START_TIME, c_s.COURSE_END_TIME AS COURSE_END_TIME, c_s.CLASSROOM AS CLASSROOM, c.TOTAL_CAPACITY AS TOTAL_CAPACITY FROM COURSE c JOIN COURSE_PROPERTY c_p ON c.COURSE_CODE = c_p.COURSE_CODE JOIN COURSE_SCHEDULE c_s ON c.COURSE_CODE = c_s.COURSE_CODE JOIN DEPARTMENT d ON c.DEPARTMENT_CODE = d.DEPARTMENT_CODE JOIN PROFESSOR p ON c.PROFESSOR_CODE = p.PROFESSOR_CODE JOIN COURSE_PROHIBIT c_ph ON c.COURSE_CODE = c_ph.COURSE_PROHIBIT WHERE TRUE
AND d.DEPARTMENT_NAME = #{courseDepartmentName}
AND c_p.GRADE = #{grade}
AND c_s.COURSE_DAY = #{courseDay}
AND c_s.COURSE_START_TIME = #{courseStartTime}
AND c.COURSE_ENGINEERING_CERTIFIED = 'Y'
AND c.DEPARTMENT_CODE != #{studentDepartmentCode}
